    private void btnDivActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDivActionPerformed
        // TODO add your handling code here:
        int nDen = Integer.parseInt(txtNum.getText());
        int nDiv = Integer.parseInt(txtDen.getText());
        
        float div = nDen / nDiv;
        float rest = nDen % nDiv;
        
        if (rest == 0) {
            lblParImpar.setText("PAR");
        } else {
            lblParImpar.setText("IMPAR");
        }
        
        lblDivisao.setText(Float.toString(div));
        lblResto.setText(Float.toString(rest));
        
    }//GEN-LAST:event_btnDivActionPerformed

